// Combien d'handles contiennent un numéro ?
function howmuchNumber(array) {
	let i = 0;
	let j = false;
	array.forEach(handle => {	
		for (let n = 0; n <= 9; n++) {
			j = false;
			if(handle.includes(n.toString())) {
				i++;
				j = true;
			}
			if(j) {
				break;
			}
		}
	})
	return i;
}

console.log("2 - Il y a " + howmuchNumber(journas) + " handles contenant un numéro.");


// Combien d'handles contiennent les 4 lettres du prénom "Aude" à la suite (sans prendre en compte les majuscules) ?
function gotAude(array) {
	let i = 0;
	array.forEach(handle => {
		handle = handle.toLowerCase();
		if (handle.includes("aude")) {
			i++;
		}
	})
	return i;
}

console.log("3 - Il y a " + gotAude(journas) + " handles contenant 'aude'.");


// Combien commencent par une majuscule (première lettre juste après le @) ?
function startWithUpcase(array) {
	let i = 0;
	array.forEach(handle => {
		if(handle[1].toUpperCase() === handle[1]) {
			i++;
		}
	})
	return i;
}

console.log("4 - Il y a " + startWithUpcase(journas) + " handles commençant par une majuscule.");


// Combien contiennent une majuscule ?
function includesUpcase(array) {
	let i = 0;
	array.forEach(handle => {
		if(handle.toLowerCase() === handle) {
			i++;
		}
	})
	let j = array.length - i;
	return j;
}

console.log("5 - Il y a " + includesUpcase(journas) + " handles contenant une majuscule.");


// Combien y a-t-il de underscore _ dans tous les pseudos confondus ?
function withUnderscore(array) {
	let i = 0;
	array.forEach(handle => {
		if (handle.includes("_")) {
			i++;
		}
	})
	return i;
}
console.log("6 - Il y a " + withUnderscore(journas) + " handles contenant un underscore type :'_'.");


// Trie la liste des handles par ordre alphabétique.
function alphabetize(array) {
	return array.sort();
}
console.log("7 - Voici la liste des handles par ordre alphabétique :");
console.log(alphabetize(journas));


// Quels sont les 50 handles les plus courts de cette array ?
function fiftyShortest(array) {
	return array.sort((a, b) => a.length - b.length).slice(0,50);
}

console.log("8 - Les 50 handles les plus cours de l'array sont :");
console.log(fiftyShortest(journas));


